import Datetime from './Datetime.vue'
import DatetimePopup from './DatetimePopup.vue'
import DatetimeCalendar from './DatetimeCalendar'
import DatetimeMonthPicker from './DatetimeMonthPicker'
import DatetimeTimePicker from './DatetimeTimePicker'
import DatetimeYearPicker from './DatetimeYearPicker'
const version = '__VERSION__'

export default {
  // eslint-disable-next-line no-unused-vars
  install: (app, options) => {
    app.component('datetime', Datetime)
    app.component('datetime-popup', DatetimePopup)
    app.component('datetime-calendar', DatetimeCalendar)
    app.component('datetime-monthPicker', DatetimeMonthPicker)
    app.component('datetime-timePicker', DatetimeTimePicker)
    app.component('datetime-yearPicker', DatetimeYearPicker)
  }
}

export {
  version,
  Datetime,
  DatetimePopup,
  DatetimeCalendar,
  DatetimeYearPicker,
  DatetimeTimePicker,
  DatetimeMonthPicker
}
